package basicJava;

public class Rectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10; //가로
		int b = 20; //세로
		int c = (2*a)+(2*b); // 둘레
		int d = a*b; // 넓이
		
		System.out.println("이 사각형의 가로는 "+a+"이고  "+"세로는 "+b+"입니다");
		System.out.println("이 사각형의 둘레는 "+c+"입니다.");
		System.out.println("이 사각형으 넓이는 "+d+"입니다.");
		
		
	}

}
