package day4;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// 비트 연산자 
		
		int num = 0B00000101;
		System.out.println(num <<2);
		System.out.println(num >>2);
		System.out.println(num >>>2);
		System.out.println(num);
		num = num<<2;
		System.out.println(num);
	}

}
